mapper.update:
UPDATE tb_scheduler_lock
SET lease_until_utc = #{leaseUntilUtc},
    updated_at = #{heartbeatAt},
    last_heartbeat_utc = #{heartbeatAt}
WHERE lock_key = #{lockKey}
  AND owner_id = #{ownerId}

UPDATE tb_data_retention_run
SET ended_at = #{endedAt},
    deleted_rows = #{deletedRows},
    status = #{status}
WHERE id = #{id}

UPDATE tb_partition_plan
SET status = #{status},
    updated_at = #{updatedAt}
WHERE table_name = #{tableName}
  AND bucket_month_utc = #{bucketMonthUtc}
