mapper.update:
UPDATE tb_data_retention_run
SET ended_at = #{endedAt},
    deleted_rows = #{deletedRows},
    status = #{status}
WHERE id = #{id}

UPDATE tb_partition_plan
SET status = #{status},
    updated_at = #{updatedAt}
WHERE table_name = #{tableName}
  AND bucket_month_utc = #{bucketMonthUtc}
