mapper.select:
SELECT id, tenant_id, conversation_id, role, message_text, trace_id, created_at
FROM tb_message
WHERE tenant_id = #{tenantId}
  AND id = #{messageId}

SELECT id, tenant_id, conversation_id, role, message_text, trace_id, created_at
FROM tb_message
WHERE tenant_id = #{tenantId}
  AND conversation_id = #{conversationId}
ORDER BY created_at ASC
